import Nav from "@/components/layout/nav";

const MyLayout = () => {
  return (
    <div>
      <Nav 
        t="pages.docs" 
        render="nav-links" 
        path="docs" 
        active 
        variant="primary" 
        dir="col" 
        pos="fixed" 
        navClass="my-custom-nav-class" 
        ulClass="my-custom-ul-class" 
        liClass="my-custom-li-class" 
      />
    </div>
  );
};

export default MyLayout;

import Nav from "@/components/layout/nav";

const MyLayout = () => {
  const links = ["Home", "About", "Contact"];
  return (
    <div>
      <Nav 
        links={links} 
        path="" 
        active 
        variant="primary" 
        dir="row" 
        pos="default" 
        navClass="my-custom-nav-class" 
        ulClass="my-custom-ul-class" 
        liClass="my-custom-li-class" 
      />
    </div>
  );
};

export default MyLayout;

import Section from "@/components/layout/section";

const MyComponent = () => {
  return (
    <Section className="my-custom-class" id="my-section">
      <h1>My Section</h1>
      <p>This is a section with custom styling.</p>
    </Section>
  );
};

export default MyComponent;

import Section from "@/components/layout/section";

const MyComponent = () => {
  return (
    <Section bgImg="/path/to/image.jpg" className="my-custom-class" id="my-section">
      <h1>My Section</h1>
      <p>This is a section with a background image.</p>
    </Section>
  );
};

export default MyComponent;

import Carousel3D from "@/components/carousel3d";

const MyComponent = () => {
  return (
    <Carousel3D imgNum={5} stopOnHover={true}>
      <img src="/path/to/image1.jpg" alt="Image 1" />
      <img src="/path/to/image2.jpg" alt="Image 2" />
      <img src="/path/to/image3.jpg" alt="Image 3" />
    </Carousel3D>
  );
};

export default MyComponent;
